# backend/main.py
from fastapi import FastAPI, Response
from fastapi.middleware.cors import CORSMiddleware
from pydantic import BaseModel
from typing import Optional
import uvicorn

app = FastAPI()

# ---- CORS -----------------------------------------------------------------
app.add_middleware(
    CORSMiddleware,
    allow_origins=[        # add your frontend URLs here
        "http://localhost:3000",
        "http://127.0.0.1:3000",
    ],
    allow_credentials=True,
    allow_methods=["*"],   # or ["POST"] if you prefer
    allow_headers=["*"],
)

# ---- Request model --------------------------------------------------------
class RequestModel(BaseModel):
    user_inquiry: str
    user_id: Optional[int] = 0          # optional / default

# ---- Fallback OPTIONS (not strictly needed once CORS middleware is active)
@app.options("/send_request")
def options_send_request() -> Response:   # bare 204 response
    return Response(status_code=204)

# ---- Main endpoint --------------------------------------------------------
@app.post("/send_request")
def send_request(request: RequestModel):
    user_inquiry = request.user_inquiry
    user_id      = request.user_id

    # Dummy response
    # response = "lorem ipsum dolor sit amet, consectetur adipiscing elit." "
    imgres="""""" 

    if "plot" in user_inquiry.lower() or "ارسم" in user_inquiry:
        return {"response": imgres, "type": "image"}

    # Save non‑plot conversation
    textres = "sample text response"
    print(f"Saved conversation: [User {user_id}] {user_inquiry} → {textres}")
    return {"response": textres, "type": "text"}

if __name__ == "__main__":
    uvicorn.run("main:app", host="127.0.0.1", port=8000, reload=True)
